import './App.css';
import Card from './components/Card';
import { useState } from 'react';
import Confirmation from './components/Confirmation';

function App() {
  const[submit, setSubmit] = useState(false);
  if(submit === false){
    return <Card setSubmit={setSubmit}/>
  }
  else {
    return <Confirmation />
  }
}

export default App;
